tasks.task:
docker pull pegi3s/pss-genome-fs:${version_genome_fast_screen}
docker pull pegi3s/check-cds:${version_check_cds}
docker pull pegi3s/ipssa:${version_ipssa}

PROJECT_DIR=${working_dir}/ipssa_project
rm -rf ${PROJECT_DIR} mkdir -p ${PROJECT_DIR} touch ${PROJECT_DIR}/compi.log
INPUT_DIR=${PROJECT_DIR}/input mkdir -p ${INPUT_DIR}
cp ${working_dir}/short_list_dir/* ${INPUT_DIR} cp ${working_dir}/${params_ipssa} ${PROJECT_DIR}/${params_ipssa} echo -e "\n\nhost_working_dir=${host_working_dir}/ipssa_project/pipeline_working_dir" >> ${PROJECT_DIR}/${params_ipssa}
PROJECT_DIR=${host_working_dir}/ipssa_project PIPELINE_WORKING_DIR=${PROJECT_DIR}/pipeline_working_dir INPUT_DIR=${PROJECT_DIR}/input PARAMS_DIR=${PROJECT_DIR}
docker run --rm \ -v /tmp:/tmp \ -v /var/run/docker.sock:/var/run/docker.sock \ -v ${PIPELINE_WORKING_DIR}:/working_dir \ -v ${INPUT_DIR}:/input \ -v ${PARAMS_DIR}:/params \ pegi3s/ipssa:${version_ipssa} \ -o --logs /working_dir/logs \ --num-tasks ${compi_num_tasks} \ -pa /params/${params_ipssa} \ 2>&1 | tee ${working_dir}/ipssa_project/compi.log
